client/src/components/SponsorInquiryForm.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";

interface InquiryFormData {
  type: string;
  companyName: string;
  contactName: string;
  email: string;
  phone: string;
  message: string;
}

interface SponsorInquiryFormProps {
  type: "corporate" | "b2b";
  title: string;
  description: string;
}

export default function SponsorInquiryForm({ type, title, description }: SponsorInquiryFormProps) {
  const { toast } = useToast();
  const [formData, setFormData] = useState<InquiryFormData>({
    type,
    companyName: "",
    contactName: "",
    email: "",
    phone: "",
    message: ""
  });

  const submitInquiry = useMutation({
    mutationFn: async (data: InquiryFormData) => {
      const response = await fetch("/api/inquiries", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error("Failed to submit inquiry");
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Inquiry sent!",
        description: "Our team will contact you within 24-48 hours."
      });
      setFormData({
        type,
        companyName: "",
        contactName: "",
        email: "",
        phone: "",
        message: ""
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send inquiry. Please try again.",
        variant: "destructive"
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    submitInquiry.mutate(formData);
  };

  return (
    <Card className="p-6">
      <h2 className="text-xl font-display font-semibold mb-2">{title}</h2>
      <p className="text-muted-foreground mb-4">{description}</p>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <Label htmlFor="companyName">Company Name</Label>
          <Input
            id="companyName"
            value={formData.companyName}
            onChange={(e) => setFormData({ ...formData, companyName: e.target.value })}
            required
          />
        </div>

        <div>
          <Label htmlFor="contactName">Contact Person</Label>
          <Input
            id="contactName"
            value={formData.contactName}
            onChange={(e) => setFormData({ ...formData, contactName: e.target.value })}
            required
          />
        </div>

        <div>
          <Label htmlFor="email">Email</Label>
          <Input
            id="email"
            type="email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
            required
          />
        </div>

        <div>
          <Label htmlFor="phone">Phone (optional)</Label>
          <Input
            id="phone"
            type="tel"
            value={formData.phone}
            onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
          />
        </div>

        <div>
          <Label htmlFor="message">Message</Label>
          <Textarea
            id="message"
            value={formData.message}
            onChange={(e) => setFormData({ ...formData, message: e.target.value })}
            rows={4}
            placeholder="Tell us about your sponsorship interests or service needs..."
            required
          />
        </div>

        <Button type="submit" className="w-full" disabled={submitInquiry.isPending}>
          {submitInquiry.isPending ? "Sending..." : "Submit Inquiry"}
        </Button>
      </form>
    </Card>
  );
}

client/src/pages/CourtsPage.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { MapPin, Phone, Building2, Users } from "lucide-react";

interface Court {
  id: string;
  name: string;
  location: string;
  university: string | null;
  city: string;
  availability: string;
  contactInfo: string;
  sponsorVisibility: number;
}

export default function CourtsPage() {
  const [cityFilter, setCityFilter] = useState("all");
  const [universityFilter, setUniversityFilter] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");

  const { data: courts = [], isLoading } = useQuery<Court[]>({
    queryKey: ['/api/courts'],
  });

  const filteredCourts = courts.filter(court => {
    const matchesCity = cityFilter === "all" || court.city === cityFilter;
    const matchesUniversity = universityFilter === "all" || court.university === universityFilter;
    const matchesSearch = court.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         court.location.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesCity && matchesUniversity && matchesSearch;
  });

  const cities = [...new Set(courts.map(c => c.city))];
  const universities = [...new Set(courts.map(c => c.university).filter(Boolean))];

  if (isLoading) {
    return <div className="flex items-center justify-center h-full">Loading courts...</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8 max-w-7xl">
      <div className="mb-8">
        <h1 className="text-3xl font-display font-bold mb-2">Training Hall</h1>
        <p className="text-muted-foreground">Find and book 3x3 basketball courts in Toulouse</p>
      </div>

      <div className="flex flex-col md:flex-row gap-4 mb-6">
        <Input
          placeholder="Search courts..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="flex-1"
        />
        <Select value={cityFilter} onValueChange={setCityFilter}>
          <SelectTrigger className="w-full md:w-48">
            <SelectValue placeholder="Filter by city" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Cities</SelectItem>
            {cities.map(city => (
              <SelectItem key={city} value={city}>{city}</SelectItem>
            ))}
          </SelectContent>
        </Select>
        <Select value={universityFilter} onValueChange={setUniversityFilter}>
          <SelectTrigger className="w-full md:w-48">
            <SelectValue placeholder="Filter by university" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Universities</SelectItem>
            {universities.map(uni => (
              <SelectItem key={uni} value={uni!}>{uni}</SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredCourts.map((court) => (
          <Card key={court.id} className="hover-elevate">
            <CardHeader>
              <div className="flex items-start justify-between">
                <CardTitle className="text-lg">{court.name}</CardTitle>
                <Badge variant={court.availability === 'available' ? 'default' : 'secondary'}>
                  {court.availability}
                </Badge>
              </div>
            </CardHeader>
            <CardContent className="space-y-3">
              <div className="flex items-start gap-2 text-sm">
                <MapPin className="w-4 h-4 mt-0.5 text-muted-foreground" />
                <div>
                  <div>{court.location}</div>
                  <div className="text-muted-foreground">{court.city}</div>
                </div>
              </div>

              {court.university && (
                <div className="flex items-center gap-2 text-sm">
                  <Building2 className="w-4 h-4 text-muted-foreground" />
                  <span>{court.university}</span>
                </div>
              )}

              <div className="flex items-center gap-2 text-sm">
                <Phone className="w-4 h-4 text-muted-foreground" />
                <span>{court.contactInfo}</span>
              </div>

              <div className="flex items-center gap-2 text-sm">
                <Users className="w-4 h-4 text-muted-foreground" />
                <span>{court.sponsorVisibility.toLocaleString()} monthly visitors</span>
              </div>

              <Button 
                className="w-full mt-4" 
                variant={court.availability === 'available' ? 'default' : 'secondary'}
                disabled={court.availability !== 'available'}
              >
                {court.availability === 'available' ? 'Book Court' : 'Currently Booked'}
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>

      {filteredCourts.length === 0 && (
        <div className="text-center py-12">
          <p className="text-muted-foreground">No courts found matching your filters</p>
        </div>
      )}
    </div>
  );
}

client/src/pages/MediaHubPage.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Upload, Video, Image as ImageIcon, Share2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";

interface Media {
  id: string;
  title: string;
  type: string;
  url: string;
  thumbnailUrl?: string;
  tournamentId?: string;
  tags?: string;
  createdAt: string;
}

export default function MediaHubPage() {
  const { toast } = useToast();
  const [uploadDialogOpen, setUploadDialogOpen] = useState(false);
  const [filter, setFilter] = useState("all");
  const [uploadData, setUploadData] = useState({
    title: "",
    type: "video",
    url: "",
    tournamentId: "",
    tags: ""
  });

  const { data: mediaList = [] } = useQuery<Media[]>({
    queryKey: ['/api/media'],
  });

  const uploadMedia = useMutation({
    mutationFn: async (data: typeof uploadData) => {
      const response = await fetch("/api/media", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error("Failed to upload");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/media'] });
      toast({
        title: "Media uploaded!",
        description: "Your content has been added to the media hub"
      });
      setUploadDialogOpen(false);
      setUploadData({ title: "", type: "video", url: "", tournamentId: "", tags: "" });
    }
  });

  const handleUpload = (e: React.FormEvent) => {
    e.preventDefault();
    uploadMedia.mutate(uploadData);
  };

  const filteredMedia = filter === "all" 
    ? mediaList 
    : mediaList.filter(m => m.type === filter);

  return (
    <div className="container mx-auto px-4 py-8 max-w-7xl">
      <div className="flex items-center justify-between mb-8">
        <div>
          <h1 className="text-3xl font-display font-bold mb-2">Media Hub</h1>
          <p className="text-muted-foreground">Share and browse tournament videos and photos</p>
        </div>
        <Dialog open={uploadDialogOpen} onOpenChange={setUploadDialogOpen}>
          <DialogTrigger asChild>
            <Button className="gap-2">
              <Upload className="w-4 h-4" />
              Upload Media
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Upload New Media</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleUpload} className="space-y-4">
              <div>
                <Label htmlFor="title">Title</Label>
                <Input
                  id="title"
                  value={uploadData.title}
                  onChange={(e) => setUploadData({ ...uploadData, title: e.target.value })}
                  placeholder="Amazing dunk from finals"
                  required
                />
              </div>
              <div>
                <Label htmlFor="type">Type</Label>
                <Select value={uploadData.type} onValueChange={(v) => setUploadData({ ...uploadData, type: v })}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="video">Video</SelectItem>
                    <SelectItem value="photo">Photo</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="url">URL (mock)</Label>
                <Input
                  id="url"
                  value={uploadData.url}
                  onChange={(e) => setUploadData({ ...uploadData, url: e.target.value })}
                  placeholder="https://example.com/media.mp4"
                  required
                />
              </div>
              <div>
                <Label htmlFor="tags">Tags (comma-separated)</Label>
                <Input
                  id="tags"
                  value={uploadData.tags}
                  onChange={(e) => setUploadData({ ...uploadData, tags: e.target.value })}
                  placeholder="dunk, finals, 2025"
                />
              </div>
              <Button type="submit" className="w-full" disabled={uploadMedia.isPending}>
                {uploadMedia.isPending ? "Uploading..." : "Upload"}
              </Button>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <Tabs value={filter} onValueChange={setFilter} className="mb-6">
        <TabsList>
          <TabsTrigger value="all">All Media</TabsTrigger>
          <TabsTrigger value="video">Videos</TabsTrigger>
          <TabsTrigger value="photo">Photos</TabsTrigger>
        </TabsList>
      </Tabs>

      {filteredMedia.length === 0 ? (
        <Card className="p-12 text-center">
          <div className="mb-4">
            {filter === "video" ? (
              <Video className="w-16 h-16 mx-auto text-muted-foreground" />
            ) : (
              <ImageIcon className="w-16 h-16 mx-auto text-muted-foreground" />
            )}
          </div>
          <h3 className="text-xl font-semibold mb-2">No media yet</h3>
          <p className="text-muted-foreground mb-4">Upload videos and photos from tournaments</p>
          <Button onClick={() => setUploadDialogOpen(true)}>
            <Upload className="w-4 h-4 mr-2" />
            Upload Media
          </Button>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredMedia.map((item) => (
            <Card key={item.id} className="overflow-hidden hover-elevate">
              <div className="aspect-video bg-muted flex items-center justify-center">
                {item.type === 'video' ? (
                  <Video className="w-12 h-12 text-muted-foreground" />
                ) : (
                  <ImageIcon className="w-12 h-12 text-muted-foreground" />
                )}
              </div>
              <CardContent className="p-4">
                <div className="flex items-start justify-between mb-2">
                  <h3 className="font-semibold line-clamp-1">{item.title}</h3>
                  <Badge variant="secondary">{item.type}</Badge>
                </div>
                {item.tags && (
                  <div className="flex flex-wrap gap-1 mb-3">
                    {item.tags.split(',').map((tag, i) => (
                      <Badge key={i} variant="outline" className="text-xs">
                        {tag.trim()}
                      </Badge>
                    ))}
                  </div>
                )}
                <div className="flex gap-2">
                  <Button variant="outline" size="sm" className="flex-1">
                    View
                  </Button>
                  <Button variant="ghost" size="sm">
                    <Share2 className="w-4 h-4" />
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}
